    private void dividActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_dividActionPerformed
        this.operador = '/';
        try {
            asignarValores(this.Pantalla.getText());
            this.existe = true;
            asd();  
        }
        catch (Exception e) {
            System.out.println("Error: " + e);
        }
    }//GEN-LAST:event_dividActionPerformed

