    private void asignarPantalla(String valor) {
        String res = this.Pantalla.getText();
        res += valor;
        this.Pantalla.setText(res);
        asd();
    }
    
    private void asignarValores(String res) {
        if (this.existe){
            this.valor2 = Double.parseDouble(res);
            this.Pantalla.setText("");
        } else {
            this.valor1 = Double.parseDouble(res);
            this.Pantalla.setText("");
        }
    } 
    
    private double calculo() {
        asd();
        switch(this.operador) {
            case '-': this.result = this.valor1 - this.valor2; break;
            case '+': this.result = this.valor1 + this.valor2; break;
            case '/': if (this.valor2 != 0) {
                this.result = this.valor1 / this.valor2;
            } else {
                 System.out.println("SyntaxError");
            }   
                break;
            case '*': this.result = this.valor1 * this.valor2; break;
            default: System.out.println("Operador no coincidente"); break;
        }
        return this.result;
    }
    
    private void asd() {
        System.out.println("El operador: " + this.operador);
        System.out.println("V1: " + valor1);
        System.out.println("V2: " + valor2);
    }

